mapper.insert:
INSERT INTO time_record(
selection_date,
member_serial_number
)
VALUES (
#{selectionDate},
#{memberSerialNumber}
);

mapper.select:
select * FROM time_record
WHERE
1=1
AND
selection_date = #{selectionDate}
AND
member_serial_number = #{memberSerialNumber}
AND
deletion_status = 'N'
limit 1;

select * FROM time_record WHERE 1=1 AND selection_date IN
#{item}
AND member_serial_number = #{memberSerialNumber} AND deletion_status = 'N'

mapper.insert:
INSERT INTO activity_record( start_datetime, end_datetime, classification_name, activity_content, goal_content, outcome_content, reflection_content, activity_score, time_record_id ) VALUES
( #{item.startDatetime}, #{item.endDatetime}, #{item.classificationName}, #{item.activityContent}, #{item.goalContent}, #{item.outcomeContent}, #{item.reflectionContent}, #{item.activityScore}, #{timeRecordId} )
;
